#JRE_HOME=$JAVA_HOME/jre
#CATALINA_PID="$CATALINA_BASE/tomcat.pid"
CATALINA_OPTS="-server -Xms384m -Xmx384m -XX:NewRatio=1 -Xss1m -XX:SurvivorRatio=6 -Djdk.nio.maxCachedBufferSize=262144 -XX:MaxDirectMemorySize=64M -XX:+UseCompressedOops -XX:+UseCompressedClassPointers -XX:CompressedClassSpaceSize=32M -XX:MetaspaceSize=64m -XX:MaxMetaspaceSize=96m -XX:MinMetaspaceExpansion=1M -XX:MaxMetaspaceExpansion=8M -XX:InitialCodeCacheSize=32M -XX:CodeCacheExpansionSize=1M -XX:CodeCacheMinimumFreeSpace=1M -XX:ReservedCodeCacheSize=64M -XX:+PrintGCDetails -Xloggc:/home/temp/tomcatBase/logs/gc.log -XX:+PrintGCDateStamps -XX:+HeapDumpOnOutOfMemoryError -XX:HeapDumpPath=/home/temp/tomcatBase/logs/heap.dump -Dcom.sun.management.jmxremote=true -Djava.rmi.server.hostname=$JMX_HOST -Dcom.sun.management.jmxremote.port=$JMX_PORT -Dcom.sun.management.jmxremote.rmi.port=$JMX_PORT -Dcom.sun.management.jmxremote.ssl=false -Dcom.sun.management.jmxremote.authenticate=false"
